import Link from 'next/link'
import Router from 'next/router'
import { Button } from 'antd'
import axios from 'axios'
import store from '../store/store'
import { connect } from 'react-redux'
import {getGoldList} from '../store/index'



const Index = ({ count, add ,goldList}) => {
  console.log('goldList==>',goldList)
  return (
    <>
      <span>首页 state的count是{count}</span>
      <button onClick={add}>增加</button>
    </>
  )
}

Index.getInitialProps = async ({ reduxStore }) => {
  console.log('state==>',reduxStore.getState())
  if (!reduxStore.getState().count) {
    reduxStore.dispatch({ type: 'add' })
  }
  reduxStore.dispatch(getGoldList())
  return {}
}

export default connect(
  state => ({goldList: state.index.data}),
  {getGoldList}
)(Index)
