import Link from 'next/link'
import Router from 'next/router'
import { Button } from 'antd'
import axios from 'axios'
import store from '../store/store'
import { connect } from 'react-redux'
import {getGoldList} from '../store/index'
import { List, Avatar } from 'antd';



const Index = ({ count, add ,goldList}) => {
  console.log('goldList==>',goldList)
  return (
    <div className='root'>
     <List
        itemLayout="horizontal"
        dataSource={goldList}
        renderItem={item => (
          <List.Item>
            <List.Item.Meta
              title={<a href="https://ant.design">{item.title}</a>}
            />
          </List.Item>
        )}
      />
      <style jsx>
          {`
            .root {
              background:#edeff1;
            }
          `}
        </style>
    </div>
  )
}

Index.getInitialProps = async ({ reduxStore }) => {
  console.log('state==>',reduxStore.getState())
  if (!reduxStore.getState().index.goldList.length) {
    reduxStore.dispatch(getGoldList())
  }
  return {}
}

export default connect(
  state => ({goldList: state.index.goldList}),
  {getGoldList}
)(Index)
